import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Compass, Users2, BadgeCheck, Search, MapPin, Calendar } from 'lucide-react';
import TripCard from '../components/TripCard';
import { tripService } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';
import { Input } from "@/components/ui/input"
import { Button } from "@/components/ui/button"
import Image from 'next/image'

const LandingPage = () => {
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    const fetchTrips = async () => {
      try {
        setLoading(true);
        const data = await tripService.getTrips();
        setTrips(data);
      } catch (err) {
        setError('Failed to fetch trips');
      } finally {
        setLoading(false);
      }
    };

    fetchTrips();
  }, []);

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="text-red-600 text-center py-8">{error}</div>;

  return (
    <div className="min-h-screen">
      {/* Hero Section with Parallax */}
      <div className="relative h-[80vh] flex items-center justify-center overflow-hidden">
        <div className="absolute inset-0 z-0">
          <Image
            src="/placeholder.svg?height=1080&width=1920"
            alt="Beautiful landscape"
            layout="fill"
            objectFit="cover"
            className="transform scale-105"
            priority
          />
          <div className="absolute inset-0 bg-black/40" />
        </div>
        
        <div className="relative z-10 max-w-4xl mx-auto text-center px-4">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            <h1 className="text-5xl md:text-6xl font-bold text-white mb-6">
              Discover Your Next Adventure
            </h1>
            <p className="text-xl md:text-2xl text-white/90 mb-8">
              Find and book amazing trips with expert guides
            </p>
            <div className="max-w-xl mx-auto flex gap-2 bg-white/10 backdrop-blur-md p-2 rounded-lg">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <Input
                  type="text"
                  placeholder="Search for trips..."
                  className="w-full pl-10 bg-white"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <Button>Search</Button>
            </div>
          </motion.div>
        </div>
      </div>

      {/* Featured Categories */}
      <div className="bg-white py-12">
        <div className="max-w-7xl mx-auto px-4">
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            {[
              { icon: MapPin, label: 'Popular Destinations' },
              { icon: Calendar, label: 'Seasonal Trips' },
              { icon: Users2, label: 'Group Adventures' },
              { icon: Compass, label: 'Custom Tours' },
            ].map((category, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.1 }}
                className="flex flex-col items-center p-6 bg-gray-50 rounded-lg hover:bg-gray-100 transition-colors"
              >
                <category.icon className="h-8 w-8 mb-3 text-primary" />
                <span className="text-sm font-medium text-gray-900">{category.label}</span>
              </motion.div>
            ))}
          </div>
        </div>
      </div>

      {/* Trips Section */}
      <div className="max-w-7xl mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold text-gray-900 mb-8">Upcoming Trips</h2>
        {trips.length === 0 ? (
          <p className="text-center text-gray-600">No trips available at the moment.</p>
        ) : (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
            className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6"
          >
            {trips
              .filter(trip => 
                trip.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                trip.location.toLowerCase().includes(searchTerm.toLowerCase())
              )
              .map((trip, index) => (
                <motion.div
                  key={trip._id}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                >
                  <TripCard trip={trip} />
                </motion.div>
              ))}
          </motion.div>
        )}
      </div>

      {/* Company Info Section */}
      <div className="bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-4">
          <div className="text-center mb-12">
            <h2 className="text-3xl font-bold text-gray-900 mb-4">
              Why Choose Exploro?
            </h2>
            <p className="text-gray-600 max-w-2xl mx-auto">
              Join thousands of satisfied travelers who have experienced the Exploro difference
            </p>
          </div>
          
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                icon: Users2,
                title: "Expert Guides",
                description: "Professional guides with years of experience",
                image: "/placeholder.svg?height=400&width=600"
              },
              {
                icon: Calendar,
                title: "Flexible Booking",
                description: "Easy cancellation and rebooking options",
                image: "/placeholder.svg?height=400&width=600"
              },
              {
                icon: BadgeCheck,
                title: "Best Prices",
                description: "Competitive prices and group discounts",
                image: "/placeholder.svg?height=400&width=600"
              }
            ].map((feature, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: index * 0.2 }}
                className="relative group"
              >
                <div className="relative h-48 mb-6 overflow-hidden rounded-lg">
                  <Image
                    src={feature.image}
                    alt={feature.title}
                    layout="fill"
                    objectFit="cover"
                    className="group-hover:scale-105 transition-transform duration-300"
                  />
                  <div className="absolute inset-0 bg-black/20 group-hover:bg-black/30 transition-colors" />
                </div>
                <div className="text-center">
                  <feature.icon className="h-8 w-8 mx-auto mb-4 text-primary" />
                  <h3 className="text-xl font-semibold text-gray-900 mb-2">{feature.title}</h3>
                  <p className="text-gray-600">{feature.description}</p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </div>

      {/* Newsletter Section */}
      <div className="bg-primary text-white py-16">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <h2 className="text-3xl font-bold mb-4">Stay Updated</h2>
          <p className="mb-8">Subscribe to our newsletter for the latest travel updates and special offers</p>
          <div className="max-w-md mx-auto flex gap-2">
            <Input
              type="email"
              placeholder="Enter your email"
              className="bg-white text-gray-900"
            />
            <Button variant="secondary">Subscribe</Button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
