import React, { useState, useContext, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { AuthContext } from "../contexts/AuthContext";
import { tripService } from "../services/api";
import LoadingSpinner from "../components/LoadingSpinner";
import ErrorMessage from "../components/ErrorMessage";
import PaymentForm from '../,./components/PaymentForm';
// Sample trip data - will be replaced with API calls later
const tripDetails = {
  id: 1,
  name: "Mountain Hiking Adventure",
  description:
    "Experience the thrill of mountain hiking in the Swiss Alps with our expert guides. This trip includes accommodation, meals, and guided tours of the most scenic routes.",
  dates: "Jan 15-22, 2025",
  price: 1299,
  availableSlots: 10,
  duration: "7 days",
  difficulty: "Moderate",
  location: "Swiss Alps",
  included: [
    "Professional guide",
    "Hotel accommodation",
    "All meals",
    "Transportation",
    "Safety equipment",
  ],
  cancellationPolicy: {
    fullRefund: 15,
    halfRefund: 7,
    noRefund: 0,
  },
  itinerary: [
    {
      day: 1,
      title: "Arrival and Orientation",
      description:
        "Arrive in Zurich, transfer to hotel, welcome dinner and trip briefing",
    },
    {
      day: 2,
      title: "First Hike",
      description: "Begin with a moderate trek to acclimatize to the altitude",
    },
  ],
};

const TripDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { user } = useContext(AuthContext);
  const [trip, setTrip] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [showBookingModal, setShowBookingModal] = useState(false);
  const [bookingInProgress, setBookingInProgress] = useState(false);

  // In TripDetails.jsx, update the useEffect
  useEffect(() => {
    const getTrip = async () => {
      try {
        setLoading(true);
        setError(null);
        const tripData = await tripService.getTripById(id);
        setTrip(tripData);
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    getTrip();
  }, [id]); // No need to include fetchTripDetails as a dependency since we moved the logic inside useEffect

  const fetchTripDetails = async () => {
    try {
      setLoading(true);
      setError(null);
      const tripData = await tripService.getTripById(id);
      setTrip(tripData);
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleBook = () => {
    if (!user) {
      navigate("/login", { state: { redirectTo: `/trips/${id}` } });
      return;
    }
    setShowBookingModal(true);
  };

  const handleConfirmBooking = async () => {
    try {
      setBookingInProgress(true);
      await tripService.createBooking(id, {
        quantity,
        totalPrice: quantity * trip.price,
        userId: user.id,
        tripDate: trip.dates,
      });
      navigate("/dashboard");
    } catch (error) {
      setError("Failed to create booking. Please try again.");
    } finally {
      setBookingInProgress(false);
      setShowBookingModal(false);
    }
  };

  if (loading) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} onRetry={fetchTripDetails} />;
  }

  if (!trip) {
    return <ErrorMessage message="Trip not found" />;
  }

  const totalPrice = quantity * trip.price;

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* Trip Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">
          {tripDetails.name}
        </h1>
        <div className="flex flex-wrap gap-4 text-gray-600">
          <span>üìç {tripDetails.location}</span>
          <span>‚è±Ô∏è {tripDetails.duration}</span>
          <span>üí™ {tripDetails.difficulty}</span>
        </div>
      </div>

      {/* Main Content Grid */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
        {/* Left Column - Trip Details */}
        <div className="lg:col-span-2 space-y-8">
          {/* Description */}
          <section>
            <h2 className="text-2xl font-semibold mb-4">About this trip</h2>
            <p className="text-gray-600">{tripDetails.description}</p>
          </section>

          {/* Itinerary */}
          <section>
            <h2 className="text-2xl font-semibold mb-4">Itinerary</h2>
            <div className="space-y-4">
              {tripDetails.itinerary.map((day) => (
                <div key={day.day} className="border-l-4 border-primary pl-4">
                  <h3 className="font-semibold">
                    Day {day.day}: {day.title}
                  </h3>
                  <p className="text-gray-600">{day.description}</p>
                </div>
              ))}
            </div>
          </section>

          {/* What's Included */}
          <section>
            <h2 className="text-2xl font-semibold mb-4">What's Included</h2>
            <ul className="grid grid-cols-1 md:grid-cols-2 gap-2">
              {tripDetails.included.map((item) => (
                <li key={item} className="flex items-center gap-2">
                  <span className="text-primary">‚úì</span>
                  {item}
                </li>
              ))}
            </ul>
          </section>
        </div>

        {/* Right Column - Booking Card */}
        <div className="lg:col-span-1">
          <div className="bg-white rounded-lg shadow-lg p-6 sticky top-4">
            <div className="mb-4">
              <h3 className="text-2xl font-bold">${tripDetails.price}</h3>
              <p className="text-gray-600">per person</p>
            </div>

            <div className="mb-4">
              <label className="block text-sm font-medium mb-2">
                Number of Travelers
              </label>
              <select
                className="w-full p-2 border rounded"
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
              >
                {[...Array(tripDetails.availableSlots)].map((_, i) => (
                  <option key={i + 1} value={i + 1}>
                    {i + 1}
                  </option>
                ))}
              </select>
            </div>

            <div className="mb-6">
              <div className="flex justify-between mb-2">
                <span>Total</span>
                <span className="font-bold">${totalPrice}</span>
              </div>
              <button
                onClick={handleBook}
                disabled={bookingInProgress}
                className={`w-full bg-primary text-white py-3 rounded-lg hover:bg-primary/90 transition-colors ${
                  bookingInProgress ? "opacity-50 cursor-not-allowed" : ""
                }`}
              >
                {bookingInProgress ? "Processing..." : "Book Now"}
              </button>
            </div>

            <div className="text-sm text-gray-600">
              <h4 className="font-medium mb-2">Cancellation Policy</h4>
              <ul className="space-y-1">
                <li>
                  ‚Ä¢ Full refund {tripDetails.cancellationPolicy.fullRefund}+
                  days before
                </li>
                <li>
                  ‚Ä¢ 50% refund {tripDetails.cancellationPolicy.halfRefund}-
                  {tripDetails.cancellationPolicy.fullRefund} days before
                </li>
                <li>
                  ‚Ä¢ No refund less than{" "}
                  {tripDetails.cancellationPolicy.halfRefund} days before
                </li>
              </ul>
            </div>
          </div>
        </div>
      </div>

      {/* Booking Modal */}
      {showBookingModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4">Confirm Booking</h2>
            <div className="space-y-4 mb-6">
              <p>
                <span className="font-medium">Trip:</span> {tripDetails.name}
              </p>
              <p>
                <span className="font-medium">Dates:</span> {tripDetails.dates}
              </p>
              <p>
                <span className="font-medium">Travelers:</span> {quantity}
              </p>
              <p>
                <span className="font-medium">Total:</span> ${totalPrice}
              </p>
            </div>
            <div className="flex gap-4">
              <button
                className="flex-1 bg-primary text-white py-2 rounded hover:bg-primary/90 disabled:opacity-50 disabled:cursor-not-allowed"
                onClick={handleConfirmBooking}
                disabled={bookingInProgress}
              >
                {bookingInProgress ? "Processing..." : "Confirm"}
              </button>
              <button
                className="flex-1 border border-gray-300 py-2 rounded hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                onClick={() => setShowBookingModal(false)}
                disabled={bookingInProgress}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default TripDetails;
