import React, { useState, useContext } from 'react';
import { AuthContext } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';


const DashboardPage = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState('upcoming');
  const [bookings, setBookings] = useState([]);

  const fetchBookings = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('/api/bookings/my', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      setBookings(data);
    } catch (error) {
      console.error('Failed to fetch bookings:', error);
    }
  };
  useEffect(() => {
    fetchBookings();
  }, []);
  const filterBookings = (status) => {
    return bookings.filter(booking => booking.status.toLowerCase() === status);
  };

  const handleCancelBooking = (bookingId) => {
    // In a real app, this would make an API call
    const updatedBookings = bookings.map(booking => 
      booking.id === bookingId 
        ? { ...booking, status: "Cancelled" }
        : booking
    );
    setBookings(updatedBookings);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      {/* User Welcome Section */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Welcome back, {user?.name || 'Traveler'}!</h1>
        <p className="text-gray-600 mt-2">Manage your trips and bookings</p>
      </div>

      {/* Dashboard Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h3 className="text-lg font-semibold text-gray-700">Total Bookings</h3>
          <p className="text-3xl font-bold text-primary mt-2">{bookings.length}</p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h3 className="text-lg font-semibold text-gray-700">Upcoming Trips</h3>
          <p className="text-3xl font-bold text-primary mt-2">
            {filterBookings('upcoming').length}
          </p>
        </div>
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h3 className="text-lg font-semibold text-gray-700">Total Spent</h3>
          <p className="text-3xl font-bold text-primary mt-2">
            ${bookings.reduce((sum, booking) => sum + booking.price * booking.participants, 0)}
          </p>
        </div>
      </div>

      {/* Bookings Section */}
      <div className="bg-white rounded-lg shadow-sm">
        <div className="border-b border-gray-200">
          <nav className="flex space-x-8 px-6" aria-label="Tabs">
            {['Upcoming', 'Pending', 'Past', 'Cancelled'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab.toLowerCase())}
                className={`py-4 px-1 border-b-2 font-medium text-sm ${
                  activeTab === tab.toLowerCase()
                    ? 'border-primary text-primary'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab}
              </button>
            ))}
          </nav>
        </div>

        <div className="p-6">
          {bookings.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-500 mb-4">No bookings found</p>
              <button
                onClick={() => navigate('/')}
                className="text-primary hover:text-primary/80"
              >
                Browse Available Trips
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              {filterBookings(activeTab).map((booking) => (
                <div key={booking.id} className="border rounded-lg p-6">
                  <div className="flex justify-between items-start">
                    <div>
                      <h3 className="text-lg font-semibold text-gray-900">
                        {booking.tripName}
                      </h3>
                      <div className="mt-2 space-y-1 text-sm text-gray-500">
                        <p>Date: {booking.date}</p>
                        <p>Participants: {booking.participants}</p>
                        <p>Booking Date: {booking.bookingDate}</p>
                        <p>Total Price: ${booking.price * booking.participants}</p>
                      </div>
                    </div>
                    <div className="flex items-center space-x-4">
                      <span className={`px-3 py-1 rounded-full text-sm
                        ${booking.status === 'Upcoming' ? 'bg-green-100 text-green-800' : ''}
                        ${booking.status === 'Pending' ? 'bg-yellow-100 text-yellow-800' : ''}
                        ${booking.status === 'Past' ? 'bg-gray-100 text-gray-800' : ''}
                        ${booking.status === 'Cancelled' ? 'bg-red-100 text-red-800' : ''}
                      `}>
                        {booking.status}
                      </span>
                      {booking.status === 'Upcoming' && (
                        <button
                          onClick={() => handleCancelBooking(booking.id)}
                          className="text-red-600 hover:text-red-800"
                        >
                          Cancel
                        </button>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default DashboardPage;
