import React, { useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../contexts/AuthContext';
import LoadingSpinner from '../components/LoadingSpinner';
import ErrorMessage from '../components/ErrorMessage';

// Sample organizer trips data
const sampleTrips = [
  {
    id: 1,
    name: "Mountain Hiking Adventure",
    dates: "Jan 15-22, 2025",
    price: 1299,
    availableSlots: 10,
    bookings: 5,
    status: "Active"
  },
  {
    id: 2,
    name: "Beach Paradise Getaway",
    dates: "Feb 1-8, 2025",
    price: 1899,
    availableSlots: 5,
    bookings: 2,
    status: "Draft"
  }
];

const OrganizerDashboard = () => {
  const { isOrganizer } = useContext(AuthContext);
  const navigate = useNavigate();
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [showAddTripModal, setShowAddTripModal] = useState(false);
  const [newTrip, setNewTrip] = useState({
    name: '',
    description: '',
    dates: '',
    price: '',
    availableSlots: '',
    location: '',
    difficulty: 'Moderate'
  });

  useEffect(() => {
    // Check if user is organizer
    if (!isOrganizer()) {
      navigate('/');
      return;
    }
    
    fetchOrganizerTrips();
  }, [isOrganizer, navigate]);

  const fetchOrganizerTrips = async () => {
    try {
      setLoading(true);
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setTrips(sampleTrips);
    } catch (error) {
      setError('Failed to fetch trips');
    } finally {
      setLoading(false);
    }
  };

  const handleAddTrip = async (e) => {
    e.preventDefault();
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));
      setTrips([...trips, { ...newTrip, id: Date.now(), status: 'Draft', bookings: 0 }]);
      setShowAddTripModal(false);
      setNewTrip({
        name: '',
        description: '',
        dates: '',
        price: '',
        availableSlots: '',
        location: '',
        difficulty: 'Moderate'
      });
    } catch (error) {
      setError('Failed to add trip');
    }
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Organizer Dashboard</h1>
          <p className="text-gray-600 mt-2">Manage your trips and bookings</p>
        </div>
        <button
          onClick={() => setShowAddTripModal(true)}
          className="bg-primary text-white px-4 py-2 rounded-lg hover:bg-primary/90"
        >
          Add New Trip
        </button>
      </div>

      {/* Trip List */}
      <div className="bg-white rounded-lg shadow overflow-hidden">
        <table className="min-w-full divide-y divide-gray-200">
          <thead className="bg-gray-50">
            <tr>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Trip Name
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Dates
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Price
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Bookings
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Status
              </th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                Actions
              </th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {trips.map((trip) => (
              <tr key={trip.id}>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm font-medium text-gray-900">{trip.name}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-500">{trip.dates}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-500">${trip.price}</div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <div className="text-sm text-gray-500">
                    {trip.bookings}/{trip.availableSlots}
                  </div>
                </td>
                <td className="px-6 py-4 whitespace-nowrap">
                  <span className={`px-2 inline-flex text-xs leading-5 font-semibold rounded-full 
                    ${trip.status === 'Active' ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}`}>
                    {trip.status}
                  </span>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                  <button className="text-primary hover:text-primary/80 mr-3">Edit</button>
                  <button className="text-red-600 hover:text-red-800">Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Add Trip Modal */}
      {showAddTripModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-2xl font-bold mb-4">Add New Trip</h2>
            <form onSubmit={handleAddTrip}>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700">Trip Name</label>
                  <input
                    type="text"
                    required
                    className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm p-2"
                    value={newTrip.name}
                    onChange={(e) => setNewTrip({ ...newTrip, name: e.target.value })}
                  />
                </div>
                {/* Add more form fields for other trip details */}
              </div>
              <div className="mt-6 flex gap-4">
                <button
                  type="submit"
                  className="flex-1 bg-primary text-white py-2 rounded hover:bg-primary/90"
                >
                  Create Trip
                </button>
                <button
                  type="button"
                  className="flex-1 border border-gray-300 py-2 rounded hover:bg-gray-50"
                  onClick={() => setShowAddTripModal(false)}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default OrganizerDashboard;
