import React, { useState, useEffect } from 'react';
import { tripService } from '../services/api';
import LoadingSpinner from '../components/LoadingSpinner';
import { MapPin, Calendar, Users, Search, Medal, Clock, CreditCard } from 'lucide-react';

const LandingPage = () => {
  const [trips, setTrips] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');

  // Sample trip data
  const sampleTrips = [
    {
      _id: '1',
      name: 'Vaishnodevi',
      location: 'Jammu & Kashmir',
      dates: 'Jan 5-8,2025',
      price: 2500,
      availableSlots: 11,
      image: '/api/placeholder/400/300?text=Vaishnodevi'
    },
    {
      _id: '2',
      name: 'Rishikesh,Haridwar',
      location: 'Uttarakhand',
      dates: 'Jan 20-23',
      price: 4000,
      availableSlots: 7,
      image: '/api/placeholder/400/300?text=Rishikesh'
    },
    {
      _id: '3',
      name: 'Vrindavan',
      location: 'Uttar Pradesh',
      dates: 'Jan 10-12',
      price: 1500,
      availableSlots: 8,
      image: '/api/placeholder/400/300?text=Vrindavan'
    }
  ];

  useEffect(() => {
    const fetchTrips = async () => {
      try {
        setLoading(true);
        // Using sample data for now
        setTrips(sampleTrips);
      } catch (err) {
        setError('Failed to fetch trips');
      } finally {
        setLoading(false);
      }
    };

    fetchTrips();
  }, []);

  if (loading) return <LoadingSpinner />;
  if (error) return <div className="text-red-600 text-center py-8">{error}</div>;

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="relative bg-blue-600 text-white">
        <div className="absolute inset-0">
          <img
            src="/api/placeholder/1920/600?text=Travel+Adventures"
            alt="Travel Background"
            className="w-full h-full object-cover mix-blend-overlay opacity-20"
          />
        </div>
        <div className="relative max-w-7xl mx-auto px-4 py-24">
          <div className="text-center max-w-3xl mx-auto">
            <h1 className="text-4xl md:text-5xl font-bold mb-6">
              Discover Your Next Adventure
            </h1>
            <p className="text-xl mb-8 text-blue-100">
              Find and book amazing trips with expert guides
            </p>
            <div className="relative max-w-xl mx-auto">
              <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                <Search className="h-5 w-5 text-gray-400" />
              </div>
              <input
                type="text"
                placeholder="Search for trips..."
                className="block w-full pl-10 pr-3 py-4 rounded-lg bg-white text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Trips Section */}
      <div className="max-w-7xl mx-auto px-4 py-16">
        <h2 className="text-3xl font-bold text-gray-900 mb-8">Upcoming Trips</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {trips.map(trip => (
            <div key={trip._id} className="bg-white rounded-xl shadow-lg overflow-hidden hover:shadow-xl transition-all duration-300">
              <div className="relative h-48">
                <img
                  src={trip.image}
                  alt={trip.name}
                  className="w-full h-full object-cover"
                />
              </div>
              <div className="p-6">
                <h3 className="text-xl font-semibold text-gray-900 mb-4">{trip.name}</h3>
                <div className="space-y-2 mb-4">
                  <div className="flex items-center text-gray-600">
                    <MapPin className="h-5 w-5 mr-2" />
                    <span>{trip.location}</span>
                  </div>
                  <div className="flex items-center text-gray-600">
                    <Calendar className="h-5 w-5 mr-2" />
                    <span>{trip.dates}</span>
                  </div>
                  <div className="flex items-center text-gray-600">
                    <Users className="h-5 w-5 mr-2" />
                    <span>{trip.availableSlots} spots left</span>
                  </div>
                </div>
                <div className="flex items-center justify-between pt-4 border-t">
                  <div className="text-2xl font-bold text-blue-600">
                    ₹{trip.price}
                  </div>
                  <button className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 transition-colors">
                    Book Now
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Features Section */}
      <div className="bg-white py-16">
        <div className="max-w-7xl mx-auto px-4">
          <h2 className="text-3xl font-bold text-gray-900 text-center mb-12">
            Why Choose Exploro?
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            <div className="text-center p-6 rounded-xl bg-blue-50 hover:bg-blue-100 transition-colors">
              <div className="inline-flex p-3 rounded-full bg-blue-100 text-blue-600 mb-4">
                <Medal className="h-8 w-8" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Expert Guides</h3>
              <p className="text-gray-600">Professional guides with years of experience</p>
            </div>
            <div className="text-center p-6 rounded-xl bg-blue-50 hover:bg-blue-100 transition-colors">
              <div className="inline-flex p-3 rounded-full bg-blue-100 text-blue-600 mb-4">
                <Clock className="h-8 w-8" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Flexible Booking</h3>
              <p className="text-gray-600">Easy cancellation and rebooking options</p>
            </div>
            <div className="text-center p-6 rounded-xl bg-blue-50 hover:bg-blue-100 transition-colors">
              <div className="inline-flex p-3 rounded-full bg-blue-100 text-blue-600 mb-4">
                <CreditCard className="h-8 w-8" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Best Prices</h3>
              <p className="text-gray-600">Competitive prices and group discounts</p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LandingPage;
