import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import LandingPage from './pages/LandingPage';

// In your Routes component, update the home route:
<Route path="/" element={<LandingPage />} />
const NavigationBar = () => (
  <nav className="bg-white shadow-lg">
    <div className="max-w-7xl mx-auto px-4">
      <div className="flex justify-between h-16">
        <div className="flex items-center">
          <span className="text-xl font-bold text-primary">Exploro</span>
        </div>
        <div className="hidden md:flex items-center space-x-4">
          <a href="/" className="text-gray-700 hover:text-primary px-3 py-2 rounded-md">Home</a>
          <a href="/trips" className="text-gray-700 hover:text-primary px-3 py-2 rounded-md">Trips</a>
          <a href="/login" className="text-gray-700 hover:text-primary px-3 py-2 rounded-md">Login</a>
          <a href="/register" className="bg-primary text-white px-4 py-2 rounded-md hover:bg-primary/90">Sign Up</a>
        </div>
      </div>
    </div>
  </nav>
);

const Layout = () => (
  <div className="min-h-screen bg-gray-50">
    <NavigationBar />
    <main className="max-w-7xl mx-auto px-4 py-6">
      <Routes>
        <Route path="/" element={<div className="text-2xl">Welcome to Exploro</div>} />
        <Route path="/trips" element={<div>Trips Page</div>} />
        <Route path="/login" element={<div>Login Page</div>} />
        <Route path="/register" element={<div>Register Page</div>} />
        <Route path="/dashboard" element={<div>Dashboard Page</div>} />
      </Routes>
    </main>
  </div>
);

const App = () => {
  return (
    <Router>
      <Layout />
    </Router>
  );
};

export default App;
